import cors from "cors";
const app = express();
app.use(express.json());
app.use(morgan("dev"));

app.use(cors());
app.use(bodyParser.json());
app.use(upload.single("image"));
app.use("/api/v1", postRoute);
app.use(bodyParser.urlencoded({ extended: false }));
app.use(upload.single("image"));
