        bearerAuth: {
          type: "http",
          scheme: "bearer",
          in: "header",
          bearerformat: "JWT",
        },
      },
    },
    securit: [
      {
        bearerAuth: [],
      },
    ],
    servers: [
      {
        url: "https://blogapi12.onrender.com",
      },
    ],
  },
  apis: ["./src/routes/*.js", "./src/modules/*.js"],
};
const specs = swaggerJsDoc(options);
app.use(cors());
app.use("/api-docs", swaggerUi.serve, swaggerUi.setup(specs));
app.use("*", (req, res) => {
  return res.status(404).json({
    status: "failed",
    message: "Invalid URL",
  });
});
export default app;

